function getSafeValue(value) {
  if (value === null || typeof value === 'undefined') {
    return "--";
  }
  if (typeof value === 'string' && value.trim() === '') {
    return "--";
  }
  return value;
}

function formatTanggal(timestampInput) {
  if (timestampInput === null || typeof timestampInput === 'undefined' || timestampInput === 0 || String(timestampInput).trim() === "") {
    return "--";
  }
  var date = new Date(timestampInput);
  if (isNaN(date.getTime())) {
    return "--";
  }
  var day = date.getDate();
  var month = date.getMonth() + 1;
  var year = date.getFullYear();
  var formattedDay = day < 10 ? '0' + day : day;
  var formattedMonth = month < 10 ? '0' + month : month;
  return formattedDay + "-" + formattedMonth + "-" + year;
};

function formatTanggalSurat(timestampInput) {
  if (timestampInput === null || typeof timestampInput === 'undefined' || timestampInput === 0 || String(timestampInput).trim() === "") {
    return "--";
  }
  var date = new Date(timestampInput);
  if (isNaN(date.getTime())) {
    return "--";
  }
  
  var months = [
    "Januari", "Februari", "Maret", "April", "Mei", "Juni",
    "Juli", "Agustus", "September", "Oktober", "November", "Desember"
  ];

  var day = date.getDate();
  var month = months[date.getMonth()];
  var year = date.getFullYear();
  
  return day + " " + month + " " + year;
};

